import React, { useState } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { 
  Plus, 
  X, 
  Calendar, 
  BookOpen, 
  Settings,
  AlertCircle,
  CheckCircle
} from 'lucide-react';

const FormContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
`;

const FormSection = styled.div`
  margin-bottom: 2rem;
`;

const SectionTitle = styled.h3`
  font-size: 1.5rem;
  font-weight: 600;
  margin-bottom: 1rem;
  color: #1a202c;
  display: flex;
  align-items: center;
  gap: 0.5rem;
`;

const FormGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 1.5rem;
  margin-bottom: 1.5rem;
`;

const FormGroup = styled.div`
  display: flex;
  flex-direction: column;
`;

const Label = styled.label`
  font-weight: 500;
  margin-bottom: 0.5rem;
  color: #374151;
  display: flex;
  align-items: center;
  gap: 0.5rem;
`;

const Required = styled.span`
  color: #ef4444;
`;

const Input = styled.input`
  padding: 0.75rem;
  border: 1px solid #d1d5db;
  border-radius: 0.5rem;
  font-size: 1rem;
  transition: all 0.2s ease;

  &:focus {
    outline: none;
    border-color: #667eea;
    box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.1);
  }

  &.error {
    border-color: #ef4444;
  }
`;

const Select = styled.select`
  padding: 0.75rem;
  border: 1px solid #d1d5db;
  border-radius: 0.5rem;
  font-size: 1rem;
  background: white;
  transition: all 0.2s ease;

  &:focus {
    outline: none;
    border-color: #667eea;
    box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.1);
  }

  &.error {
    border-color: #ef4444;
  }
`;

const ErrorMessage = styled.div`
  color: #ef4444;
  font-size: 0.875rem;
  margin-top: 0.25rem;
  display: flex;
  align-items: center;
  gap: 0.25rem;
`;

const CompletedCoursesSection = styled.div`
  background: #f8fafc;
  border-radius: 0.75rem;
  padding: 1.5rem;
  margin-bottom: 1.5rem;
`;

const CourseInputContainer = styled.div`
  display: flex;
  gap: 1rem;
  margin-bottom: 1rem;
  align-items: end;
`;

const CourseInput = styled.input`
  flex: 1;
  padding: 0.75rem;
  border: 1px solid #d1d5db;
  border-radius: 0.5rem;
  font-size: 1rem;

  &:focus {
    outline: none;
    border-color: #667eea;
    box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.1);
  }
`;

const AddButton = styled.button`
  background: #667eea;
  color: white;
  border: none;
  padding: 0.75rem 1rem;
  border-radius: 0.5rem;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  font-weight: 500;
  transition: all 0.2s ease;

  &:hover {
    background: #5a67d8;
    transform: translateY(-1px);
  }
`;

const CourseList = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
`;

const CourseTag = styled.div`
  background: white;
  border: 1px solid #e5e7eb;
  border-radius: 0.5rem;
  padding: 0.5rem 1rem;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  font-size: 0.875rem;
  font-weight: 500;
`;

const RemoveButton = styled.button`
  background: none;
  border: none;
  color: #6b7280;
  cursor: pointer;
  padding: 0.25rem;
  border-radius: 0.25rem;
  transition: all 0.2s ease;

  &:hover {
    background: #f3f4f6;
    color: #ef4444;
  }
`;

const PreferencesSection = styled.div`
  background: #f8fafc;
  border-radius: 0.75rem;
  padding: 1.5rem;
`;

const PreferenceGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 1.5rem;
`;

const CheckboxContainer = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
`;

const Checkbox = styled.input`
  width: 1.25rem;
  height: 1.25rem;
  accent-color: #667eea;
`;

const CheckboxLabel = styled.label`
  font-weight: 500;
  color: #374151;
  cursor: pointer;
`;

const SubmitButton = styled.button`
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  border: none;
  padding: 1rem 2rem;
  border-radius: 0.75rem;
  font-size: 1.125rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  margin: 2rem auto 0;
  min-width: 200px;

  &:hover:not(:disabled) {
    transform: translateY(-2px);
    box-shadow: 0 10px 25px -5px rgba(102, 126, 234, 0.4);
  }

  &:disabled {
    opacity: 0.6;
    cursor: not-allowed;
  }
`;

const HelpText = styled.p`
  font-size: 0.875rem;
  color: #6b7280;
  margin-top: 0.5rem;
  line-height: 1.5;
`;

const ExampleCourses = styled.div`
  margin-top: 1rem;
  padding: 1rem;
  background: #f0f9ff;
  border-radius: 0.5rem;
  border-left: 4px solid #667eea;
`;

const ExampleTitle = styled.h4`
  font-size: 0.875rem;
  font-weight: 600;
  color: #1e40af;
  margin-bottom: 0.5rem;
`;

const ExampleList = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
`;

const ExampleCourse = styled.span`
  background: white;
  color: #1e40af;
  padding: 0.25rem 0.5rem;
  border-radius: 0.25rem;
  font-size: 0.75rem;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s ease;

  &:hover {
    background: #667eea;
    color: white;
  }
`;

function DegreePlanForm({ register, handleSubmit, errors, watch, setValue, onSubmit, isLoading, error }) {
  const [newCourse, setNewCourse] = useState('');
  const completedCourses = watch('completedCourses') || [];

  const addCourse = () => {
    if (newCourse.trim() && !completedCourses.includes(newCourse.trim().toUpperCase())) {
      setValue('completedCourses', [...completedCourses, newCourse.trim().toUpperCase()]);
      setNewCourse('');
    }
  };

  const removeCourse = (courseToRemove) => {
    setValue('completedCourses', completedCourses.filter(course => course !== courseToRemove));
  };

  const addExampleCourse = (course) => {
    if (!completedCourses.includes(course)) {
      setValue('completedCourses', [...completedCourses, course]);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      addCourse();
    }
  };

  const exampleCourses = [
    'COSC 1306', 'COSC 1436', 'MATH 1431', 'ENGL 1301', 'HIST 1301'
  ];

  return (
    <FormContainer>
      <form onSubmit={handleSubmit(onSubmit)}>
        <FormSection>
          <SectionTitle>
            <BookOpen size={24} />
            Basic Information
          </SectionTitle>
          <FormGrid>
            <FormGroup>
              <Label>
                Student Major
                <Required>*</Required>
              </Label>
              <Select 
                {...register('studentMajor', { required: 'Major is required' })}
                className={errors.studentMajor ? 'error' : ''}
              >
                <option value="Computer Science">Computer Science</option>
              </Select>
              {errors.studentMajor && (
                <ErrorMessage>
                  <AlertCircle size={16} />
                  {errors.studentMajor.message}
                </ErrorMessage>
              )}
            </FormGroup>

            <FormGroup>
              <Label>
                Start Semester
                <Required>*</Required>
              </Label>
              <Select 
                {...register('startSemester', { required: 'Start semester is required' })}
                className={errors.startSemester ? 'error' : ''}
              >
                <option value="Fall">Fall</option>
                <option value="Spring">Spring</option>
                <option value="Summer">Summer</option>
              </Select>
              {errors.startSemester && (
                <ErrorMessage>
                  <AlertCircle size={16} />
                  {errors.startSemester.message}
                </ErrorMessage>
              )}
            </FormGroup>

            <FormGroup>
              <Label>
                Start Year
                <Required>*</Required>
              </Label>
              <Input
                type="number"
                {...register('startYear', { 
                  required: 'Start year is required',
                  min: { value: 2020, message: 'Year must be 2020 or later' },
                  max: { value: 2030, message: 'Year must be 2030 or earlier' }
                })}
                className={errors.startYear ? 'error' : ''}
              />
              {errors.startYear && (
                <ErrorMessage>
                  <AlertCircle size={16} />
                  {errors.startYear.message}
                </ErrorMessage>
              )}
            </FormGroup>
          </FormGrid>
        </FormSection>

        <FormSection>
          <SectionTitle>
            <CheckCircle size={24} />
            Completed Courses
          </SectionTitle>
          <CompletedCoursesSection>
            <CourseInputContainer>
              <CourseInput
                type="text"
                placeholder="Enter course code (e.g., COSC 1436)"
                value={newCourse}
                onChange={(e) => setNewCourse(e.target.value)}
                onKeyPress={handleKeyPress}
              />
              <AddButton type="button" onClick={addCourse}>
                <Plus size={20} />
                Add Course
              </AddButton>
            </CourseInputContainer>

            {completedCourses.length > 0 && (
              <CourseList>
                {completedCourses.map((course, index) => (
                  <CourseTag key={index}>
                    {course}
                    <RemoveButton 
                      type="button" 
                      onClick={() => removeCourse(course)}
                    >
                      <X size={16} />
                    </RemoveButton>
                  </CourseTag>
                ))}
              </CourseList>
            )}

            <ExampleCourses>
              <ExampleTitle>Example courses you might have taken:</ExampleTitle>
              <ExampleList>
                {exampleCourses.map((course) => (
                  <ExampleCourse 
                    key={course}
                    onClick={() => addExampleCourse(course)}
                  >
                    {course}
                  </ExampleCourse>
                ))}
              </ExampleList>
            </ExampleCourses>
          </CompletedCoursesSection>
        </FormSection>

        <FormSection>
          <SectionTitle>
            <Settings size={24} />
            Preferences
          </SectionTitle>
          <PreferencesSection>
            <PreferenceGrid>
              <FormGroup>
                <Label>Max Credits per Semester</Label>
                <Select {...register('preferences.maxCreditHoursPerSemester')}>
                  <option value={12}>12 credits</option>
                  <option value={13}>13 credits</option>
                  <option value={14}>14 credits</option>
                  <option value={15}>15 credits</option>
                  <option value={16}>16 credits</option>
                  <option value={17}>17 credits</option>
                  <option value={18}>18 credits</option>
                </Select>
                <HelpText>Maximum credit hours you want to take per semester</HelpText>
              </FormGroup>

              <FormGroup>
                <Label>Min Credits per Semester</Label>
                <Select {...register('preferences.minCreditHoursPerSemester')}>
                  <option value={9}>9 credits</option>
                  <option value={10}>10 credits</option>
                  <option value={11}>11 credits</option>
                  <option value={12}>12 credits</option>
                  <option value={13}>13 credits</option>
                  <option value={14}>14 credits</option>
                  <option value={15}>15 credits</option>
                </Select>
                <HelpText>Minimum credit hours you want to take per semester</HelpText>
              </FormGroup>

              <FormGroup>
                <Label>Difficulty Preference</Label>
                <Select {...register('preferences.difficultyPreference')}>
                  <option value="Balanced">Balanced</option>
                  <option value="Easier">Easier</option>
                  <option value="Challenging">Challenging</option>
                </Select>
                <HelpText>How you want to balance course difficulty</HelpText>
              </FormGroup>

              <FormGroup>
                <Label>Workload Preference</Label>
                <Select {...register('preferences.workloadPreference')}>
                  <option value="Balanced">Balanced</option>
                  <option value="Lighter">Lighter</option>
                  <option value="Heavier">Heavier</option>
                </Select>
                <HelpText>How you want to balance course workload</HelpText>
              </FormGroup>

              <FormGroup>
                <CheckboxContainer>
                  <Checkbox
                    type="checkbox"
                    {...register('preferences.avoidSummerClasses')}
                    defaultChecked
                  />
                  <CheckboxLabel>Avoid Summer Classes</CheckboxLabel>
                </CheckboxContainer>
                <HelpText>Try to avoid scheduling courses in summer semesters</HelpText>
              </FormGroup>
            </PreferenceGrid>
          </PreferencesSection>
        </FormSection>

        {error && (
          <motion.div
            initial={{ opacity: 0, y: -10 }}
            animate={{ opacity: 1, y: 0 }}
            style={{
              background: '#fef2f2',
              border: '1px solid #fecaca',
              borderRadius: '0.5rem',
              padding: '1rem',
              marginBottom: '1rem',
              color: '#dc2626',
              display: 'flex',
              alignItems: 'center',
              gap: '0.5rem'
            }}
          >
            <AlertCircle size={20} />
            {error}
          </motion.div>
        )}

        <SubmitButton type="submit" disabled={isLoading}>
          {isLoading ? (
            <>
              <div className="loading" />
              Generating Plan...
            </>
          ) : (
            <>
              <BookOpen size={20} />
              Generate Degree Plan
            </>
          )}
        </SubmitButton>
      </form>
    </FormContainer>
  );
}

export default DegreePlanForm;
