import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { 
  GraduationCap, 
  Brain, 
  Database, 
  Users, 
  Target,
  CheckCircle,
  ArrowRight,
  BookOpen,
  TrendingUp,
  Shield,
  Zap
} from 'lucide-react';

const PageContainer = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  padding: 2rem 0;
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 1rem;
`;

const PageHeader = styled.div`
  text-align: center;
  margin-bottom: 4rem;
  color: white;
`;

const PageTitle = styled.h1`
  font-size: 3.5rem;
  font-weight: 700;
  margin-bottom: 1.5rem;
  background: linear-gradient(135deg, #ffffff 0%, #f0f9ff 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;

  @media (max-width: 768px) {
    font-size: 2.5rem;
  }
`;

const PageSubtitle = styled.p`
  font-size: 1.25rem;
  opacity: 0.9;
  max-width: 600px;
  margin: 0 auto;
  line-height: 1.6;
`;

const ContentCard = styled(motion.div)`
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(10px);
  border-radius: 1.5rem;
  padding: 3rem;
  box-shadow: 0 20px 40px -10px rgba(0, 0, 0, 0.3);
  border: 1px solid rgba(255, 255, 255, 0.2);
  margin-bottom: 2rem;
`;

const SectionTitle = styled.h2`
  font-size: 2rem;
  font-weight: 700;
  margin-bottom: 1.5rem;
  color: #1a202c;
  display: flex;
  align-items: center;
  gap: 0.75rem;
`;

const SectionContent = styled.div`
  color: #4a5568;
  line-height: 1.7;
  font-size: 1.125rem;
`;

const FeaturesGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 2rem;
  margin: 2rem 0;
`;

const FeatureCard = styled(motion.div)`
  background: #f8fafc;
  border-radius: 1rem;
  padding: 2rem;
  border: 1px solid #e2e8f0;
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 10px 25px -5px rgba(0, 0, 0, 0.1);
  }
`;

const FeatureIcon = styled.div`
  width: 60px;
  height: 60px;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  border-radius: 1rem;
  display: flex;
  align-items: center;
  justify-content: center;
  margin-bottom: 1.5rem;
  color: white;
`;

const FeatureTitle = styled.h3`
  font-size: 1.25rem;
  font-weight: 600;
  margin-bottom: 1rem;
  color: #1a202c;
`;

const FeatureDescription = styled.p`
  color: #64748b;
  line-height: 1.6;
`;

const StatsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 2rem;
  margin: 2rem 0;
`;

const StatCard = styled.div`
  text-align: center;
  padding: 2rem;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  border-radius: 1rem;
`;

const StatNumber = styled.div`
  font-size: 3rem;
  font-weight: 700;
  margin-bottom: 0.5rem;
`;

const StatLabel = styled.div`
  font-size: 1.125rem;
  opacity: 0.9;
`;

const TechStack = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 1.5rem;
  margin: 2rem 0;
`;

const TechCard = styled.div`
  background: #f8fafc;
  border: 1px solid #e2e8f0;
  border-radius: 0.75rem;
  padding: 1.5rem;
  text-align: center;
`;

const TechIcon = styled.div`
  width: 50px;
  height: 50px;
  background: #667eea;
  border-radius: 0.75rem;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 0 auto 1rem;
  color: white;
`;

const TechName = styled.h4`
  font-size: 1.125rem;
  font-weight: 600;
  margin-bottom: 0.5rem;
  color: #1a202c;
`;

const TechDescription = styled.p`
  font-size: 0.875rem;
  color: #64748b;
  line-height: 1.5;
`;

const CTAButton = styled.button`
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  border: none;
  padding: 1rem 2rem;
  border-radius: 0.75rem;
  font-size: 1.125rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  margin: 2rem auto 0;
  text-decoration: none;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 10px 25px -5px rgba(102, 126, 234, 0.4);
  }
`;

const List = styled.ul`
  list-style: none;
  padding: 0;
  margin: 1rem 0;
`;

const ListItem = styled.li`
  display: flex;
  align-items: center;
  gap: 0.75rem;
  margin-bottom: 0.75rem;
  color: #4a5568;
  font-size: 1.125rem;
`;

const CheckIcon = styled.div`
  width: 24px;
  height: 24px;
  background: #10b981;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  flex-shrink: 0;
`;

function About() {
  const features = [
    {
      icon: <Brain size={32} />,
      title: 'AI-Powered Planning',
      description: 'Leverages Google Gemini AI to create intelligent, personalized degree plans based on your academic history and preferences.'
    },
    {
      icon: <Database size={32} />,
      title: 'Real-Time Data',
      description: 'Integrates with CougarGrades.io to provide up-to-date course information, professor ratings, and GPA statistics.'
    },
    {
      icon: <Target size={32} />,
      title: 'Balanced Workload',
      description: 'Ensures each semester has an optimal mix of easy and challenging courses for sustainable academic progress.'
    },
    {
      icon: <Shield size={32} />,
      title: 'Prerequisite Tracking',
      description: 'Automatically manages course prerequisites and ensures proper sequencing throughout your academic journey.'
    },
    {
      icon: <TrendingUp size={32} />,
      title: 'Performance Analytics',
      description: 'Uses historical data to predict course difficulty and help you make informed decisions about your schedule.'
    },
    {
      icon: <Zap size={32} />,
      title: 'Instant Generation',
      description: 'Generate comprehensive degree plans in seconds, saving you hours of manual planning and research.'
    }
  ];

  const techStack = [
    {
      icon: <Brain size={24} />,
      name: 'Google Gemini AI',
      description: 'Advanced AI for intelligent course planning and optimization'
    },
    {
      icon: <Database size={24} />,
      name: 'CougarGrades API',
      description: 'Real-time course data and professor ratings'
    },
    {
      icon: <BookOpen size={24} />,
      name: 'React Frontend',
      description: 'Modern, responsive user interface'
    },
    {
      icon: <TrendingUp size={24} />,
      name: 'Node.js Backend',
      description: 'Scalable server architecture'
    }
  ];

  return (
    <PageContainer>
      <Container>
        <PageHeader>
          <PageTitle>About CougarDegree</PageTitle>
          <PageSubtitle>
            Revolutionizing Computer Science degree planning at the University of Houston
          </PageSubtitle>
        </PageHeader>

        <ContentCard
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          <SectionTitle>
            <GraduationCap size={32} />
            Our Mission
          </SectionTitle>
          <SectionContent>
            <p>
              CougarDegree was born from the recognition that Computer Science students at the University of Houston 
              need a smarter way to plan their academic journey. Traditional degree planning is time-consuming, 
              error-prone, and doesn't take into account the real-world factors that affect student success.
            </p>
            <p>
              Our platform combines the power of artificial intelligence with real-time academic data to create 
              personalized, balanced degree plans that help students graduate on time while maintaining a healthy 
              academic workload.
            </p>
          </SectionContent>
        </ContentCard>

        <ContentCard
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.1 }}
        >
          <SectionTitle>
            <Target size={32} />
            Key Features
          </SectionTitle>
          <FeaturesGrid>
            {features.map((feature, index) => (
              <FeatureCard
                key={index}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: index * 0.1 }}
                whileHover={{ scale: 1.05 }}
              >
                <FeatureIcon>
                  {feature.icon}
                </FeatureIcon>
                <FeatureTitle>{feature.title}</FeatureTitle>
                <FeatureDescription>{feature.description}</FeatureDescription>
              </FeatureCard>
            ))}
          </FeaturesGrid>
        </ContentCard>

        <ContentCard
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.2 }}
        >
          <SectionTitle>
            <Users size={32} />
            How It Works
          </SectionTitle>
          <SectionContent>
            <List>
              <ListItem>
                <CheckIcon>
                  <CheckCircle size={16} />
                </CheckIcon>
                <div>
                  <strong>Input Your Information:</strong> Tell us your major, start semester, and any courses you've already completed.
                </div>
              </ListItem>
              <ListItem>
                <CheckIcon>
                  <CheckCircle size={16} />
                </CheckIcon>
                <div>
                  <strong>AI Analysis:</strong> Our system analyzes your profile using Google Gemini AI and CougarGrades data.
                </div>
              </ListItem>
              <ListItem>
                <CheckIcon>
                  <CheckCircle size={16} />
                </CheckIcon>
                <div>
                  <strong>Generate Plan:</strong> Receive a detailed, semester-by-semester degree plan optimized for your needs.
                </div>
              </ListItem>
              <ListItem>
                <CheckIcon>
                  <CheckCircle size={16} />
                </CheckIcon>
                <div>
                  <strong>Track Progress:</strong> Monitor your academic progress and adjust your plan as needed.
                </div>
              </ListItem>
            </List>
          </SectionContent>
        </ContentCard>

        <ContentCard
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.3 }}
        >
          <SectionTitle>
            <Database size={32} />
            Technology Stack
          </SectionTitle>
          <TechStack>
            {techStack.map((tech, index) => (
              <TechCard key={index}>
                <TechIcon>
                  {tech.icon}
                </TechIcon>
                <TechName>{tech.name}</TechName>
                <TechDescription>{tech.description}</TechDescription>
              </TechCard>
            ))}
          </TechStack>
        </ContentCard>

        <ContentCard
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.4 }}
        >
          <SectionTitle>
            <TrendingUp size={32} />
            Impact & Statistics
          </SectionTitle>
          <StatsGrid>
            <StatCard>
              <StatNumber>4</StatNumber>
              <StatLabel>Years to Graduate</StatLabel>
            </StatCard>
            <StatCard>
              <StatNumber>15</StatNumber>
              <StatLabel>Credits per Semester</StatLabel>
            </StatCard>
            <StatCard>
              <StatNumber>120+</StatNumber>
              <StatLabel>CS Courses Available</StatLabel>
            </StatCard>
            <StatCard>
              <StatNumber>100%</StatNumber>
              <StatLabel>AI Optimized</StatLabel>
            </StatCard>
          </StatsGrid>
        </ContentCard>

        <ContentCard
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.5 }}
        >
          <SectionTitle>
            <BookOpen size={32} />
            Data Sources
          </SectionTitle>
          <SectionContent>
            <p>
              CougarDegree relies on several trusted data sources to provide accurate and up-to-date information:
            </p>
            <List>
              <ListItem>
                <CheckIcon>
                  <CheckCircle size={16} />
                </CheckIcon>
                <div>
                  <strong>CougarGrades.io:</strong> Historical GPA data, professor ratings, and course difficulty metrics
                </div>
              </ListItem>
              <ListItem>
                <CheckIcon>
                  <CheckCircle size={16} />
                </CheckIcon>
                <div>
                  <strong>UH Course Catalog:</strong> Official course descriptions, prerequisites, and credit hours
                </div>
              </ListItem>
              <ListItem>
                <CheckIcon>
                  <CheckCircle size={16} />
                </CheckIcon>
                <div>
                  <strong>Google Gemini AI:</strong> Advanced AI for intelligent course planning and optimization
                </div>
              </ListItem>
            </List>
          </SectionContent>
        </ContentCard>

        <div style={{ textAlign: 'center', marginTop: '2rem' }}>
          <CTAButton as="a" href="/degree-plan">
            Start Planning Your Degree
            <ArrowRight size={20} />
          </CTAButton>
        </div>
      </Container>
    </PageContainer>
  );
}

export default About;
