import React, { useState } from 'react';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import { useForm } from 'react-hook-form';
import { 
  GraduationCap, 
  Calendar, 
  BookOpen, 
  CheckCircle, 
  Clock,
  TrendingUp,
  Users,
  Star,
  ArrowRight,
  Download,
  RefreshCw
} from 'lucide-react';
import DegreePlanForm from '../components/DegreePlanForm';
import DegreePlanResults from '../components/DegreePlanResults';
import LoadingSpinner from '../components/LoadingSpinner';

const PageContainer = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  padding: 2rem 0;
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 1rem;
`;

const PageHeader = styled.div`
  text-align: center;
  margin-bottom: 3rem;
  color: white;
`;

const PageTitle = styled.h1`
  font-size: 3rem;
  font-weight: 700;
  margin-bottom: 1rem;
  background: linear-gradient(135deg, #ffffff 0%, #f0f9ff 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;

  @media (max-width: 768px) {
    font-size: 2.5rem;
  }
`;

const PageSubtitle = styled.p`
  font-size: 1.25rem;
  opacity: 0.9;
  max-width: 600px;
  margin: 0 auto;
  line-height: 1.6;
`;

const ContentCard = styled(motion.div)`
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(10px);
  border-radius: 1.5rem;
  padding: 2rem;
  box-shadow: 0 20px 40px -10px rgba(0, 0, 0, 0.3);
  border: 1px solid rgba(255, 255, 255, 0.2);
`;

const TabContainer = styled.div`
  display: flex;
  margin-bottom: 2rem;
  background: #f8fafc;
  border-radius: 0.75rem;
  padding: 0.25rem;
`;

const Tab = styled.button`
  flex: 1;
  padding: 1rem;
  border: none;
  background: ${props => props.active ? 'white' : 'transparent'};
  color: ${props => props.active ? '#667eea' : '#64748b'};
  font-weight: 600;
  border-radius: 0.5rem;
  cursor: pointer;
  transition: all 0.2s ease;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 0.5rem;

  &:hover {
    color: #667eea;
  }
`;

const EmptyState = styled.div`
  text-align: center;
  padding: 4rem 2rem;
  color: #64748b;
`;

const EmptyStateIcon = styled.div`
  width: 80px;
  height: 80px;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  border-radius: 1rem;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 0 auto 1.5rem;
  color: white;
`;

const EmptyStateTitle = styled.h3`
  font-size: 1.5rem;
  font-weight: 600;
  margin-bottom: 1rem;
  color: #1a202c;
`;

const EmptyStateDescription = styled.p`
  font-size: 1.125rem;
  margin-bottom: 2rem;
  line-height: 1.6;
`;

const ActionButton = styled.button`
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  padding: 1rem 2rem;
  border-radius: 0.75rem;
  border: none;
  font-weight: 600;
  font-size: 1.125rem;
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  margin: 0 auto;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 10px 25px -5px rgba(102, 126, 234, 0.4);
  }
`;

const StatsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 1.5rem;
  margin-bottom: 2rem;
`;

const StatCard = styled.div`
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  padding: 1.5rem;
  border-radius: 1rem;
  text-align: center;
  box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
`;

const StatIcon = styled.div`
  width: 40px;
  height: 40px;
  background: rgba(255, 255, 255, 0.2);
  border-radius: 0.75rem;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 0 auto 1rem;
`;

const StatNumber = styled.div`
  font-size: 2rem;
  font-weight: 700;
  margin-bottom: 0.5rem;
`;

const StatLabel = styled.div`
  font-size: 0.875rem;
  opacity: 0.9;
`;

function DegreePlan() {
  const [activeTab, setActiveTab] = useState('form');
  const [degreePlan, setDegreePlan] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const { register, handleSubmit, formState: { errors }, watch, setValue } = useForm({
    defaultValues: {
      studentMajor: 'Computer Science',
      startSemester: 'Fall',
      startYear: new Date().getFullYear(),
      completedCourses: [],
      preferences: {
        maxCreditHoursPerSemester: 15,
        minCreditHoursPerSemester: 12,
        difficultyPreference: 'Balanced',
        workloadPreference: 'Balanced',
        avoidSummerClasses: true
      }
    }
  });

  const onSubmit = async (data) => {
    setIsLoading(true);
    setError(null);

    try {
      const response = await fetch('/api/degree-plan/generate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
      });

      if (!response.ok) {
        throw new Error('Failed to generate degree plan');
      }

      const result = await response.json();
      setDegreePlan(result);
      setActiveTab('results');
    } catch (err) {
      setError(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  const handleRegenerate = () => {
    setActiveTab('form');
    setDegreePlan(null);
    setError(null);
  };

  const handleDownload = () => {
    if (!degreePlan) return;

    const dataStr = JSON.stringify(degreePlan, null, 2);
    const dataUri = 'data:application/json;charset=utf-8,'+ encodeURIComponent(dataStr);

    const exportFileDefaultName = `degree-plan-${new Date().toISOString().split('T')[0]}.json`;

    const linkElement = document.createElement('a');
    linkElement.setAttribute('href', dataUri);
    linkElement.setAttribute('download', exportFileDefaultName);
    linkElement.click();
  };

  const calculateStats = () => {
    if (!degreePlan) return null;

    const totalCredits = degreePlan.totalCreditHours || 0;
    const completedCredits = degreePlan.completedCourses?.reduce((sum, course) => sum + (course.creditHours || 0), 0) || 0;
    const remainingCredits = totalCredits - completedCredits;
    const totalSemesters = degreePlan.semesters?.length || 0;

    return {
      totalCredits,
      completedCredits,
      remainingCredits,
      totalSemesters,
      progress: Math.round((completedCredits / totalCredits) * 100)
    };
  };

  const stats = calculateStats();

  return (
    <PageContainer>
      <Container>
        <PageHeader>
          <PageTitle>Degree Planning</PageTitle>
          <PageSubtitle>
            Create your personalized UH Computer Science degree plan with AI-powered recommendations
          </PageSubtitle>
        </PageHeader>

        <ContentCard
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          <TabContainer>
            <Tab 
              active={activeTab === 'form'} 
              onClick={() => setActiveTab('form')}
            >
              <BookOpen size={20} />
              Plan Your Degree
            </Tab>
            <Tab 
              active={activeTab === 'results'} 
              onClick={() => setActiveTab('results')}
              disabled={!degreePlan}
            >
              <CheckCircle size={20} />
              Your Plan
            </Tab>
          </TabContainer>

          <AnimatePresence mode="wait">
            {activeTab === 'form' && (
              <motion.div
                key="form"
                initial={{ opacity: 0, x: -20 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: 20 }}
                transition={{ duration: 0.3 }}
              >
                {!degreePlan ? (
                  <DegreePlanForm
                    register={register}
                    handleSubmit={handleSubmit}
                    errors={errors}
                    watch={watch}
                    setValue={setValue}
                    onSubmit={onSubmit}
                    isLoading={isLoading}
                    error={error}
                  />
                ) : (
                  <EmptyState>
                    <EmptyStateIcon>
                      <GraduationCap size={40} />
                    </EmptyStateIcon>
                    <EmptyStateTitle>Ready to Generate a New Plan?</EmptyStateTitle>
                    <EmptyStateDescription>
                      You already have a degree plan. Would you like to create a new one with different preferences?
                    </EmptyStateDescription>
                    <ActionButton onClick={handleRegenerate}>
                      <RefreshCw size={20} />
                      Generate New Plan
                    </ActionButton>
                  </EmptyState>
                )}
              </motion.div>
            )}

            {activeTab === 'results' && degreePlan && (
              <motion.div
                key="results"
                initial={{ opacity: 0, x: 20 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: -20 }}
                transition={{ duration: 0.3 }}
              >
                {stats && (
                  <StatsGrid>
                    <StatCard>
                      <StatIcon>
                        <BookOpen size={20} />
                      </StatIcon>
                      <StatNumber>{stats.totalCredits}</StatNumber>
                      <StatLabel>Total Credits</StatLabel>
                    </StatCard>
                    <StatCard>
                      <StatIcon>
                        <CheckCircle size={20} />
                      </StatIcon>
                      <StatNumber>{stats.completedCredits}</StatNumber>
                      <StatLabel>Completed</StatLabel>
                    </StatCard>
                    <StatCard>
                      <StatIcon>
                        <Clock size={20} />
                      </StatIcon>
                      <StatNumber>{stats.remainingCredits}</StatNumber>
                      <StatLabel>Remaining</StatLabel>
                    </StatCard>
                    <StatCard>
                      <StatIcon>
                        <Calendar size={20} />
                      </StatIcon>
                      <StatNumber>{stats.totalSemesters}</StatNumber>
                      <StatLabel>Semesters</StatLabel>
                    </StatCard>
                  </StatsGrid>
                )}

                <DegreePlanResults 
                  degreePlan={degreePlan}
                  onRegenerate={handleRegenerate}
                  onDownload={handleDownload}
                />
              </motion.div>
            )}
          </AnimatePresence>

          {isLoading && (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              style={{ textAlign: 'center', padding: '2rem' }}
            >
              <LoadingSpinner />
              <p style={{ marginTop: '1rem', color: '#64748b' }}>
                Generating your personalized degree plan...
              </p>
            </motion.div>
          )}
        </ContentCard>
      </Container>
    </PageContainer>
  );
}

export default DegreePlan;
